第二章 LuaFramework/2_3 CallLuaFunction.htm.body:
调用Lua方法
CallLuaFunction.cs
    //#define TEST_GC
    using UnityEngine;
    using System.Collections;
    using LuaInterface;
    using System;

    public class CallLuaFunction : MonoBehaviour 
    {
        private string script = 
            @"  function luaFunc(num)                        
                    return num + 1
                end

